import { useEffect, useState } from "react";
import { useParams, NavLink } from "react-router-dom";
import utilities from "../js/utilities";

function EditBook() {
  const [book, setBook] = useState({});
  const [authors, setAuthors] = useState([]);
  let { bookId } = useParams();

  useEffect(() => {
    async function fetchData() {
      var tempBook = await utilities.fetchBook(bookId);
      setBook(tempBook);

      var tempAuthors = await utilities.fetchAuthors();
      tempAuthors = tempAuthors.map((x) => (
        <option value="author_id">{x.first_name + " " + x.last_name}</option>
      ));
      setAuthors(tempAuthors);
    }
    fetchData();
  }, [bookId]);

  function getAuthorName() {
    if (book != undefined && book.author != undefined)
      return book.author.first_name + " " + book.author.last_name;
    return "";
  }

  function submitForm(e) {
    e.preventDefault()  
    console.log('called')
  }

  return (
    <>
      <form onSubmit={submitForm}>
        <div className="row mb-5">
          <div className="col-12">
            <NavLink to={"/"} className="navlink">
              &lt;&lt; Back to list
            </NavLink>
            <NavLink to={"/book/" + book.book_id} className="navlink ms-2">
              View
            </NavLink>
          </div>
        </div>
        <div className="row">
          <div className="col-12">
            <h1>Edit book</h1>
          </div>
        </div>
        <div className="row">
          <div className="col-3 d-flex align-items-center justify-content-end">
            Title
          </div>
          <div className="col-9">
            <input
              className="form-control"
              value={book.name}
              onChange={(e) => setBook({ ...book, name: e.target.value })}
            />
          </div>
        </div>
        <div className="row mt-2">
          <div className="col-3 d-flex align-items-center justify-content-end">
            ISBN
          </div>
          <div className="col-9">
            <input
              className="form-control"
              value={book.isbn}
              onChange={(e) => setBook({ ...book, isbn: e.target.value })}
            />
          </div>
        </div>
        <div className="row mt-2">
          <div className="col-3 d-flex align-items-center justify-content-end">
            Author
          </div>
          <div className="col-9">
            <select
              value={book.author_id}
              className="form-control"
              onChange={(e) => setBook({ ...book, author_id: e.target.value })}
            >
              {authors}
            </select>
          </div>
        </div>
        <div className="row mt-2">
          <div class="col-9 offset-3">
            <button class="btn btn-primary">Update</button>
          </div>
        </div>
      </form>
    </>
  );
}

export default EditBook;
